import React, { FC } from "react";
import { ICartItem } from "../redux/slices/pizzaSlice";
import { useSelector } from "react-redux";
import { RootState } from "../redux/store";
import { current } from "@reduxjs/toolkit";
import { useAddCartItemMutation, useRemoveCartItemMutation } from "../API/api";

const CartItemBlock: FC<ICartItem> = ({
  id,
  url,
  title,
  types,
  sizes,
  price,
}) => {
  const cartItems = useSelector(
    (state: RootState) => state.cartItemsState.cartItems
  );

  const countPizza = cartItems.reduce(
    (count, item) => (item.title === title ? ++count : count),
    0
  );

  const [addCartItem] = useAddCartItemMutation();

  const [removeCartItem, { isLoading, isSuccess, error }] =
    useRemoveCartItemMutation();

  return (
    <div className="content__items">
      <div className="cart__item">
        <div className="cart__item-img">
          <img className="pizza-block__image" src={url} alt="Pizza" />
        </div>
        <div className="cart__item-info">
          <h3>{title}</h3>
          <p>{types == 0 ? "тонкое" : "традиционное"} тесто</p>
          <p>{sizes == 0 ? 26 : sizes == 1 ? 30 : 40}см.</p>
        </div>
        <div className="cart__item-count">
          <div
            className="button button--outline button--circle cart__item-count-minus"
            onClick={async () => {
              console.log("удалено");
              // dispatch(onAddToCart(newCartItem));
              await removeCartItem(id)
                .then((response) => {
                  console.log("Пицца успешно удалена", response);
                })
                .catch((error) => {
                  alert(error);
                });
            }}
          >
            <svg
              width="10"
              height="10"
              viewBox="0 0 10 10"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                d="M5.92001 3.84V5.76V8.64C5.92001 9.17016 5.49017 9.6 4.96001 9.6C4.42985 9.6 4.00001 9.17016 4.00001 8.64L4 5.76L4.00001 3.84V0.96C4.00001 0.42984 4.42985 0 4.96001 0C5.49017 0 5.92001 0.42984 5.92001 0.96V3.84Z"
                fill="#EB5A1E"
              />
              <path
                d="M5.75998 5.92001L3.83998 5.92001L0.959977 5.92001C0.429817 5.92001 -2.29533e-05 5.49017 -2.29301e-05 4.96001C-2.2907e-05 4.42985 0.429817 4.00001 0.959977 4.00001L3.83998 4L5.75998 4.00001L8.63998 4.00001C9.17014 4.00001 9.59998 4.42985 9.59998 4.96001C9.59998 5.49017 9.17014 5.92001 8.63998 5.92001L5.75998 5.92001Z"
                fill="#EB5A1E"
              />
            </svg>
          </div>
          <b>{countPizza}</b>
          <div
            className="button button--outline button--circle cart__item-count-plus"
            onClick={async () => {
              console.log("Добавлено");
              const newCartItem = {
                id: id,
                url: url,
                title: title,
                types: types,
                sizes: sizes,
                price: price,
              };
              // dispatch(onAddToCart(newCartItem));
              await addCartItem(newCartItem)
                .then((response) => {
                  console.log("Пицца успешно добавлена", response);
                })
                .catch((error) => {
                  console.error("Ошибка добавления пиццы", error);
                });
            }}
          >
            <svg
              width="10"
              height="10"
              viewBox="0 0 10 10"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                d="M5.92001 3.84V5.76V8.64C5.92001 9.17016 5.49017 9.6 4.96001 9.6C4.42985 9.6 4.00001 9.17016 4.00001 8.64L4 5.76L4.00001 3.84V0.96C4.00001 0.42984 4.42985 0 4.96001 0C5.49017 0 5.92001 0.42984 5.92001 0.96V3.84Z"
                fill="#EB5A1E"
              />
              <path
                d="M5.75998 5.92001L3.83998 5.92001L0.959977 5.92001C0.429817 5.92001 -2.29533e-05 5.49017 -2.29301e-05 4.96001C-2.2907e-05 4.42985 0.429817 4.00001 0.959977 4.00001L3.83998 4L5.75998 4.00001L8.63998 4.00001C9.17014 4.00001 9.59998 4.42985 9.59998 4.96001C9.59998 5.49017 9.17014 5.92001 8.63998 5.92001L5.75998 5.92001Z"
                fill="#EB5A1E"
              />
            </svg>
          </div>
        </div>
        <div className="cart__item-price">
          <b>{price * countPizza} ₽</b>
        </div>
        <div className="cart__item-remove">
          <div className="button button--outline button--circle" onClick={async ()=> {
            for (let idex = 0; index < countPizza; index++) {
               await removeCartItem()
                
            }
          }}>
            <svg
              width="10"
              height="10"
              viewBox="0 0 10 10"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                d="M5.92001 3.84V5.76V8.64C5.92001 9.17016 5.49017 9.6 4.96001 9.6C4.42985 9.6 4.00001 9.17016 4.00001 8.64L4 5.76L4.00001 3.84V0.96C4.00001 0.42984 4.42985 0 4.96001 0C5.49017 0 5.92001 0.42984 5.92001 0.96V3.84Z"
                fill="#EB5A1E"
              />
              <path
                d="M5.75998 5.92001L3.83998 5.92001L0.959977 5.92001C0.429817 5.92001 -2.29533e-05 5.49017 -2.29301e-05 4.96001C-2.2907e-05 4.42985 0.429817 4.00001 0.959977 4.00001L3.83998 4L5.75998 4.00001L8.63998 4.00001C9.17014 4.00001 9.59998 4.42985 9.59998 4.96001C9.59998 5.49017 9.17014 5.92001 8.63998 5.92001L5.75998 5.92001Z"
                fill="#EB5A1E"
              />
            </svg>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CartItemBlock;
