import { createApi, fetchBaseQuery } from "@reduxjs/toolkit/query/react";
import { IPizzaRequest } from "../types";
import { IPizzaItem } from "../redux/slices/pizzaSlice";

export const PizzaApi = createApi({
  reducerPath: "PizzaApi",
  baseQuery: fetchBaseQuery({ baseUrl: "http://localhost:3000/" }),
  tagTypes: ["Pizzas", "CartItems", "CartItem"],
  endpoints: (builder) => ({
    getAllPizzas: builder.query<IPizzaItem[], IPizzaRequest>({
      query: (request) => ({
        url: "items?",
        method: "GET",
        params: {
          ...request,
        },
      }),
      providesTags: () => [{ type: "Pizzas" }],
    }),
    getCartItems: builder.query({
      query: () => ({
        url: "cartItems?",
        method: "GET",
      }),
      providesTags: () => [{ type: "CartItems" }],
    }),
    addCartItem: builder.mutation({
      query: (newItem) => ({
        url: '/posts', 
        method: 'POST',
        body: newItem,
      }),
      // Опционально:  обработчик успешного ответа (fulfilled) 
      //  или обработчик ошибки (rejected) 
      //  для обновления состояния после успешной/неудачной мутации
      invalidatesTags: ['CartItem'], //  Очищает кэш для всех запросов с тегом 'Posts'
    }),
  }),
});

export const { useGetAllPizzasQuery, useGetCartItemsQuery } = PizzaApi;
