import React from "react";

const Categories = () => {
  const [currentCategory, setCurrentCategory] = React.useState(0);

  const onClickCategory = (index : number) => {
    setCurrentCategory(index);
}

  return (
    <div className="categories">
      <ul>
        <li onClick = {() => setCurrentCategory(0)} 
        className={currentCategory === 0 ? "active" : ''}>Все</li>
        <li onClick = {() => setCurrentCategory(1)} className={currentCategory === 1 ? "active" : ''}>Мясные</li>
        <li onClick = {() => setCurrentCategory(2)} className={currentCategory === 2 ? "active" : ''}>Вегетарианская</li>
        <li onClick = {() => setCurrentCategory(3)} className={currentCategory === 3 ? "active" : ''}>Гриль</li>
        <li onClick = {() => setCurrentCategory(4)} className={currentCategory === 4 ? "active" : ''}>Острые</li>
        <li onClick = {() => setCurrentCategory(5)} className={currentCategory === 5 ? "active" : ''}>Закрытые</li>
      </ul>
    </div>
  );
};

export default Categories;
