import { createSlice } from "@reduxjs/toolkit";
import { error } from "console";
import { stat } from "fs";
import { act } from "react-dom/test-utils";
import CartItemBlock from "../../components_TS/CartItemBlock";
import { PizzaApi } from "../../API/api";

export interface ICartItem {
  id: string;
  url: string;
  title: string;
  types: number;
  sizes: number;
  price: number;
}

export interface ICartItemList {
  cartItems: ICartItem[];
  isLoading: boolean;
  error: string | null;
}

const initialState: ICartItemList = {
  cartItems: [],
  isLoading: false,
  error: null,
};

const filterSlice = createSlice({
  name: "filter",
  initialState,
  reducers: {
    onAddToCart: (state, action) => {
      state.cartItems = [...state.cartItems, action.payload];
      console.log(action.type);
    },
  },
  extraReducers(builder) {
    builder.addMatcher(
      PizzaApi.endpoints.getCartItems.matchFulfilled,
      (state, action) => {
        state.cartItems = action.payload;
        state.isLoading
      }
    );
  },
});

export const { onAddToCart } =
  filterSlice.actions;

export default filterSlice.reducer;
