import { createSlice } from "@reduxjs/toolkit";
import { types } from "util";
import { PizzaApi } from "../../API/api";

export interface IPizzaItem {
  id: string;
  url: string;
  title: string;
  types: number[];
  sizes: number[];
  price: number;
  category: number;
  rating: number;
}

export interface ICartItem {
  id: string;
  url: string;
  title: string;
  types: number;
  sizes: number;
  price: number;
}

export interface IPizzas {
  pizzaItems: IPizzaItem[];
  IsLoadingReady: boolean;
  cartItems: ICartItem[];
}

const initialState: IPizzas = {
  pizzaItems: [],
  cartItems: [],
  IsLoadingReady: true,
};

export const pizzaSlice = createSlice({
  name: "pizzaSlice",
  initialState: initialState,
  reducers: {
    pushPizzaItems: (state, actions) => {
      state.pizzaItems = actions.payload;
    },
    setIsLoadingReady: (state, action) => {
      state.IsLoadingReady = action.payload;
    },

  },
  extraReducers (builder) {
    builder
    .addMatcher(PizzaApi.endpoints.g.matchFulfilled,
      (state, action) => {
        state.cartItems = action.payload;
        state.isLoading = false;
        state.error = undefined;
      })
  }
});

// Action creators are generated for each case reducer function
export const { pushPizzaItems, setIsLoadingReady } =
  pizzaSlice.actions;

export default pizzaSlice.reducer;
