import { createApi, fetchBaseQuery } from "@reduxjs/toolkit/query/react";
import { IPizzaRequest } from "../types";

export const PizzaApi = createApi({
  reducerPath: "PizzaApi",
  baseQuery: fetchBaseQuery({ baseUrl: "http://localhost:3000/" }),
  tagTypes: ["Pizzas"],
  endpoints: (builder) => ({
    getAllPizzas: builder.query<IPizzaItem>({
      query: (request: IPizzaRequest) => ({
        url: "items?",
        // url: `items?${
        //   reqProp.category.value == 0 ? "" : `category=${reqProp.category.value}&`
        // }
        // _sort=${reqProp.sortItem.property }`,
        method: "GET",
        params: {
          ...request,
        },
      }),
      providesTags: () => [{ type: "Pizzas" }],
    }),
  }),
});

export const { useGetAllPizzasQuery } = PizzaApi;
