const Categories = () => {
  const categories = [
    "Все",
    "Мясные",
    "Вегетарианские",
    "Гриль",
    "Острые",
    "Закрытые",
  ];

  const dispatch = useDispatch();
  const curentSort = useSelector((state: RootState) => state.filterState.sortItem);

