import { createSlice } from "@reduxjs/toolkit";

interface ISort {
  name: string;
  property: string;
}

interface IFilter {
  text: string;
  value: number;
}
interface ISortList {
  sortItem: ISort;
  filterItem: IFilter;
}

const initialState: ISortList = {
  sortItem: {
    text: "Все",
    value: 0,
  },
  filterItem: {
    name: "популярности",
    property: "rating",
  },
};

const filterSlice = createSlice({
  name: "filter",
  initialState,
  reducers: {
    setPizzaFilterState(state, action) {
      state.sortItem = action.payload;
      console.log(state.sortItem);
    },
  },
});

export const { setPizzaFilterState } = filterSlice.actions;

export default filterSlice.reducer;
