export const AppContext = React.createContext("");

const onAddToCart = () => {};

function App() {
  //логика redux
  const count = useSelector((state) => state.counter.value);
  const dispatch = useDispatch();

  const [IsLoadingReady, setIsLoadingReady] = React.useState(false);
  const [items, setItems] = React.useState([]);
  const [cartItems, setCartItems] = React.useState([]);
  // сортировка и категории
  const [currentCategory, setCurrentCategory] = React.useState(0);
  const [currentSort, setCurrentSort] = React.useState({
    name: "популярности",
    property: "rating",
  });
  // поиск
  const [searchValue, setSearchValue] = React.useState("");

  const sort = currentCategory > 0 ? "=" + currentCategory : "";

  React.useEffect(() => {
    async function fetchData() {
      setIsLoadingReady(false);
      const itemsResponse = await axios.get(
        `http://localhost:3000/items?category${sort}
        &_sort=${currentSort.property}`
      );

      setItems(itemsResponse.data);
      const cartItemsResponse = await axios.get(
        "http://localhost:3000/cartItems"
      );

      setCartItems(cartItemsResponse.data);
      setTimeout(() => {
        setIsLoadingReady(true);
      }, 200);
    }

    fetchData();

    window.scrollTo(0, 0);
  }, [currentCategory, currentSort]);

  const onChangeSearchValue = (event) => {
    setSearchValue(event.target.value);
  };

  return (
    <div>
      <div>
        <button
          aria-label="Increment value"
          onClick={() => dispatch(increment())}
        >
          Increment
        </button>
        <span>{count}</span>
        <button
          aria-label="Decrement value"
          onClick={() => dispatch(decrement())}
        >
          Decrement
        </button>
      </div>
    </div>

    // <AppContext.Provider
