    //     setCurrentSort,
    //     searchValue,
    //     setSearchValue,
    //     onChangeSearchValue,
    //   }}
    // >
    //   <div className="wrapper">
    //     <Header />
    //     <div className="content">
    //       <Routes>
    //         <Route path="/" element={<PizzaPage />} />
    //         <Route path="/cart" element={<CartPage />} />
    //         <Route path="*" element={<NotFound />}></Route>
    //       </Routes>
    //     </div>
    //   </div>
    // </AppContext.Provider>
  );
}

export default App;
