import { createApi, fetchBaseQuery } from "@reduxjs/toolkit/query/react";

export const PizzaApi = createApi({
  reducerPath: "PizzaApi",
  baseQuery: fetchBaseQuery({ baseUrl: "http://localhost:3000/" }),
  tagTypes: ["Pizzas"],
  endpoints: (builder) => ({
    getAllPizzas: builder.query({
      query: (reqProp) => ({
        url: `http://localhost:3000/items?${reqProp.category == 0? '': "category=${"}
        &_sort=${currentSort.property}`,
        method: "GET",
        // params: {
        //   category: reqProp.category,
        //   _sort: reqProp.sortItem,
        // },
      }),
      providesTags: () => [{ type: "Pizzas" }],
    }),
  }),
});

export const { useGetAllPizzasQuery } = PizzaApi;
