import React from "react";
import { Pizza } from "./models/pizza";
import { Routes, Route } from "react-router";

import "./scss/app.scss";

import Header from "./components_TS/Header";
import Sort from "./components_TS/Sort";
import Categories from "./components_TS/Categories";
import PizzaBlock from "./components_TS/PizzaBlock/PizzaBlock";
import axios from "axios";
import PizzaPage from "./pages/Pizza.page";
import CartPage from "./pages/Cart.page";
import NotFound from "./pages/Not.Found";

export const AppContext = React.createContext<Context>({
  IsLoadingReady: false,
  items: [],
  cartItems: [],
});

interface Context {
  IsLoadingReady: boolean;
  items: Pizza[];
  cartItems: any[];
}

function App() {
  const [IsLoadingReady, setIsLoadingReady] = React.useState(false);
  const [items, setItems] = React.useState<Pizza[]>([]);
  const [cartItems, setCartItems] = React.useState([]);

  React.useEffect(() => {
    async function fetchData() {
      setIsLoadingReady(false);
      const itemsResponse = await axios.get("http://localhost:3000/items");

      setItems(itemsResponse.data);
      const cartItemsResponse = await axios.get(
        "http://localhost:3000/cartItems"
      );

      setCartItems(cartItemsResponse.data);
      setTimeout(() => {
        setIsLoadingReady(true);
      }, 1000);
    }

    fetchData();

  }, []);

  return (
    <AppContext.Provider
      value={{
        IsLoadingReady,
        items,
        cartItems,
      }}
    >
      <div className="wrapper">
        <Header />
        <div className="content">
          <Routes>
            <Route path="/" element={<PizzaPage />} />
            <Route path="/cart" element={<CartPage />} />
            <Route path="*" element={<NotFound />}></Route>
          </Routes>
        </div>
      </div>
    </AppContext.Provider>
  );
}

export default App;
